package cn.activity.service;

import cn.activity.dao.UserMapper;
import cn.activity.domain.User;
import org.mybatis.spring.SqlSessionTemplate;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.HttpRequest;
import org.springframework.stereotype.Service;
import sun.misc.BASE64Decoder;

import javax.servlet.ServletContext;
import java.io.File;
import java.io.FileOutputStream;
import java.io.OutputStream;
import java.util.List;

@Service
public class UserService {
    @Autowired
    public SqlSessionTemplate sqlSessionTemplate;


    public boolean cheackPassword(String stuNum,String password){
        UserMapper userMapper = sqlSessionTemplate.getMapper(UserMapper.class);
        List<User> users = userMapper.getUserByStuNumber(stuNum);
        if (users == null){
            return false;
        }else{
            User u = users.get(0);
            if (u.getPassword().equals(password))
                return true;
        }
        return false;
    }

    public List<User> getAllUserByNumber(String number){
        UserMapper userMapper = sqlSessionTemplate.getMapper(UserMapper.class);
       return userMapper.getUserByStuNumber(number);
    }

    public User getUserByStuNum(String studentNumber){
        UserMapper userMapper = sqlSessionTemplate.getMapper(UserMapper.class);
        List<User> users = userMapper.getUserByStuNumber(studentNumber);
        return users.get(0);
    }

    public int insertUser(User u){
        UserMapper userMapper = sqlSessionTemplate.getMapper(UserMapper.class);
        int id = userMapper.insert(u);
        return id;
    }
   /* public int updateByPrimaryKey(User user){
        UserMapper userMapper = sqlSessionTemplate.getMapper(UserMapper.class);
        return userMapper.updateByPrimaryKey(user);
    }*/

   public int update(User user){
       UserMapper userMapper = sqlSessionTemplate.getMapper(UserMapper.class);
       return userMapper.update(user);
   }

    public void saveUserHead(int userId ,ServletContext context,String value/*HttpRequest request, int userId , String value*/){
       //String value = "";
       /*String path = "user\\"+userId+ ".jpg";*/
       String path = context.getRealPath("/");
       File img = new File(path+"user"+File.separator+userId+ ".jpg");
       System.out.println(path+"user"+File.separator+userId+ ".jpg");
       BASE64Decoder decoder = new BASE64Decoder();
        try
        {
            if(!img.exists()){
                img.createNewFile();
            }
            //Base64解码
            byte[] b = decoder.decodeBuffer(value);
            for(int i=0;i<b.length;++i)
            {
                if(b[i]<0)
                {//调整异常数据
                    b[i]+=256;
                }
            }
            OutputStream out = new FileOutputStream(img);
            out.write(b);
            out.flush();
            out.close();
        }
        catch (Exception e)
        {
            e.printStackTrace();
        }
    }
}
